class Article {
  String id;
  String author;
  String category;
  String createdAt;
  String desc;
  List<String> images;
  int likeCounts;
  String publishedAt;
  int stars;
  String title;
  String type;
  String url;
  int views;

  Article({
      this.id, 
      this.author, 
      this.category, 
      this.createdAt, 
      this.desc, 
      this.images, 
      this.likeCounts, 
      this.publishedAt, 
      this.stars, 
      this.title, 
      this.type, 
      this.url, 
      this.views});

  Article.fromJson(dynamic json) {
    id = json["_id"];
    author = json["author"];
    category = json["category"];
    createdAt = json["createdAt"];
    desc = json["desc"];
    images = json["images"] != null ? json["images"].cast<String>() : [];
    likeCounts = json["likeCounts"];
    publishedAt = json["publishedAt"];
    stars = json["stars"];
    title = json["title"];
    type = json["type"];
    url = json["url"];
    views = json["views"];
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    map["_id"] = id;
    map["author"] = author;
    map["category"] = category;
    map["createdAt"] = createdAt;
    map["desc"] = desc;
    map["images"] = images;
    map["likeCounts"] = likeCounts;
    map["publishedAt"] = publishedAt;
    map["stars"] = stars;
    map["title"] = title;
    map["type"] = type;
    map["url"] = url;
    map["views"] = views;
    return map;
  }

}